      request.getMaxResults();

  params[drive.ds.sync.api.Params.Request.INCLUDE_DELETED] =
      request.getIncludeDeleted();

  params[drive.ds.sync.api.Params.Request.INCLUDE_SUBSCRIBED] =
      request.getIncludeSubscribed();

