  var tx = this.db_.createTransaction(
      [drive.ds.sync.cache.IndexedDbCache.OBJECT_STORE_]);
  var store = tx.objectStore(drive.ds.sync.cache.IndexedDbCache.OBJECT_STORE_);

  return this.deferredDeserialize_(store.get(id));
