  INFO: 'INFO',

  INITIALIZE: 'INITIALIZE',

  INVALIDATION_ACK: 'INVALIDATION_ACK',

  REGISTRATION: 'REGISTRATION',

  REGISTRATION_SYNC: 'REGISTRATION_SYNC',

