  INVALIDATION: 'INVALIDATION',

  REGISTRATION_STATUS: 'REGISTRATION_STATUS',

  REGISTRATION_SYNC_REQUEST: 'REGISTRATION_SYNC_REQUEST',

  TOKEN_CONTROL: 'TOKEN_CONTROL',

